mapper.select:
SELECT COUNT(me_id) as cnt
FROM IEP_MEMBER
WHERE me_id=#{me_id}

SELECT COUNT(me_nick) as cnt
FROM IEP_MEMBER
WHERE me_nick=#{me_nick}

SELECT count(me_no) as cnt
FROM iep_member
where  me_id=#{me_id} AND me_pw=#{me_pw}

mapper.update:
UPDATE iep_member
SET me_zipcode=#{me_zipcode}, me_addr1=#{me_addr1}, me_addr2=#{me_addr2},
me_tel=#{me_tel}
WHERE me_no=#{me_no}

mapper.select:
SELECT me_no, me_nick, me_name, me_grade
FROM IEP_MEMBER
where me_id=#{me_id}

mapper.update:
update iep_member
set me_vis = #{me_vis}
where me_no=#{me_no}

update iep_member
set me_vis =  #{me_vis}
where me_no=#{me_no}

mapper.select:
SELECT COUNT(me_no) as cnt
FROM iep_member
WHERE me_no=#{me_no} AND me_pw=#{me_pw}

mapper.update:
UPDATE iep_member
SET me_pw=#{me_pw}
WHERE me_no=#{me_no}
