mapper.update:
UPDATE commu_meet
SET cm_title=#{cm_title}, cm_content=#{cm_content}, file1=#{file1}, file2=#{file2}, size2=#{size2},
    cm_map=#{cm_map}, cm_url=#{cm_url}
WHERE cm_no=#{cm_no}

UPDATE commu_meet
SET cm_cnt = cm_cnt + 1
WHERE cm_no=#{cm_no}

UPDATE commu_meet
SET ansnum = ansnum + 1
WHERE grpno = #{grpno} AND ansnum > #{ansnum}
